mapper.select:
select

from ess_enterprise
where id = #{id,jdbcType=VARCHAR}

		SELECT COUNT(1) FROM ess_enterprise WHERE 1=1
		
			and etpNm = #{etpnm}
		
		
			and state = #{state}
		
		
			and left(createTime,10) >= #{referStartTime}
		
		
			and left(createTime,10) <= #{referEndTime}
		

		
			and left(updateTime,10) >= #{checkStartTime}
		
		
			and left(updateTime,10) <= #{checkEndTime}

		SELECT
		etpNm,etpAdress,etpTel,etpSafer,saferTel,etpLawer,lawerTel,createTime,updateTime,state
		FROM ess_enterprise
		WHERE 1=1 and (isDeleted is null or isDeleted = 0)
		
			and etpNm = #{etpnm}
		
		
			and state = #{state}
		
		
			and left(createTime,10) >= #{referStartTime}
		
		
			and left(createTime,10) <= #{referEndTime}
		

		
			and left(updateTime,10) >= #{checkStartTime}
		
		
			and left(updateTime,10) <= #{checkEndTime}
		
		order by createTime desc
		limit ${startRecord},${pageSize}

SELECT v.id, e.etpNm, v.vehiNo, CASE v.vehiNoType WHEN '01' THEN '大型车' ELSE '小型车' END AS vehiNoType, v.nature, v.firstReciveTime, v.examineEffectEndTime AS effectEndTime, v. OWNER, v.mobilephone, v.telphone, v.mgrDepart, v.state, a.areaNm, v.ownership, v.adress, v.examineEffectEndTime, v.identityCode, v.identityName, CASE v.mbCar WHEN 1 THEN '是' WHEN 2 THEN '否' ELSE '不明确' END AS mbCar, v.vehicleState, v.examineForceEffectEndTime, CASE v.countrySideUse WHEN '1' THEN '是' WHEN '2' THEN '否' ELSE '不明确' END AS countrySideUse FROM ess_area a LEFT JOIN vehicle v ON a.id = v.mgrDepartAreaId LEFT JOIN ess_enterprise e ON v.mgrEnterpriseId = e.id
WHERE v.vehiNo= #{vehiNo}
WHERE 1=1
WHERE a.id in
#{item}
WHERE en.id = #{etpId}
AND (v.isDeleted is null OR v.isDeleted !=1)
AND e.etpNm like '%${etpNm}%'
AND a.areaNm= #{areaNm}
AND v.id= #{id}
AND v.vehiNoType= #{vehiNoType}
AND e.id!='' LIMIT ${startRecord},${pageSize}

SELECT
count(v.id )
FROM
ess_area a
LEFT JOIN vehicle v ON a.id =
v.mgrDepartAreaId
LEFT JOIN
ess_enterprise e ON v.mgrEnterpriseId = e.id

	
		WHERE v.vehiNo= #{vehiNo}
	
	
		WHERE 1=1
	
	
		WHERE a.id in
		
			#{item}
		
	
	
		WHERE en.id = #{etpId}
	

AND (v.isDeleted is null OR v.isDeleted !=1)

	AND e.etpNm like '%${etpNm}%'


	AND a.areaNm= #{areaNm}


	AND v.id= #{id}


	AND v.vehiNoType= #{vehiNoType}

AND e.id!=''

SELECT d.id, d.adress, d. NAME, d.idcard, d.mobilephone, d.permiCar, d.firstReciveTime, d.nextExamineTime, d.state, d.mgrDepart, d.telphone, d.scoreTotal, d.examineEffectEndTime, d.effectStartTime, d.effectEndTime, e.etpNm, a.areaNm, d.state1 FROM driver d LEFT JOIN ess_enterprise e ON d.mgrEnterpriseId = e.id LEFT JOIN ess_area a ON d.mgrDepartAreaId = a.id
WHERE d.idcard= #{idcard}
WHERE 1=1
WHERE a.id in
#{item}
WHERE en.id = #{etpId}
AND e.etpNm like '%${etpNm}%'
AND a.areaNm= #{areaNm}
AND d.id= #{id}
AND e.id !='' AND (d.isDelete is null OR d.isDelete !=1) LIMIT ${startRecord},${pageSize}

SELECT count(d.id) FROM driver d LEFT JOIN ess_enterprise e ON d.mgrEnterpriseId = e.id LEFT JOIN ess_area a ON d.mgrDepartAreaId = a.id
WHERE d.idcard= #{idcard}
WHERE 1=1
WHERE a.id in
#{item}
WHERE en.id = #{etpId}
AND e.etpNm like '%${etpNm}%'
AND a.areaNm= #{areaNm}
AND d.id= #{id}
AND e.id !='' AND (d.isDelete is null OR d.isDelete !=1)

mapper.update:
UPDATE vehicle
SET
mgrEnterpriseId = null WHERE id = #{id}

mapper.select:
SELECT COUNT(IF(danger.scrappedState='1',true,null))as scrappedCount, COUNT(IF(danger.illicitState='1',true,null))as illicitCount, COUNT(IF(danger.overdueExamineState='1',true,null))as vehicleExamineCount FROM vehi_danger danger LEFT JOIN vehicle ve ON ve.id=danger.vehicle_id LEFT JOIN ess_enterprise ent ON ent.id=ve.mgrEnterpriseId WHERE 1=1
and ent.id in
#{item.id}

SELECT COUNT(DISTINCT danger.driver_id, IF(danger.overdueProofState='1',true,null)) as overdueProofCount, COUNT(DISTINCT danger.driver_id, IF(danger.overdueExamineState='1',true,null)) as DrExamineCount, COUNT(DISTINCT danger.driver_id, IF(danger.fullStudyState='1',true,null)) as fullStudyCount FROM dr_danger danger LEFT JOIN driver dr ON danger.driver_id=dr.id LEFT JOIN ess_enterprise ent ON ent.id=dr.mgrEnterpriseId WHERE 1=1
and ent.id in
#{item.id}
