mapper.select:
SELECT post.id AS id,org.orgNm AS orgNm,org.id AS orgid,depart.depNm AS depNm,post.postCd AS postCd,post.postNm AS postNm,post.description AS description ,post.dep_id AS depId FROM ess_department depart,ess_organization org,ess_post post WHERE depart.org_id=org.id AND post.dep_id=depart.id AND post.isDeleted!=1 AND org.id =#{orgId}
SELECT post.id AS id,etp.etpNm AS orgNm,etp.id AS orgid,depart.depNm AS depNm,post.postCd AS postCd,post.postNm AS postNm,post.description AS description ,post.dep_id AS depId FROM ess_department depart,ess_enterprise etp,ess_post post WHERE depart.ept_id=etp.id AND post.dep_id=depart.id AND post.isDeleted!=1 AND etp.id =#{etpId}
SELECT post.id AS id,org.orgNm AS orgNm,org.id AS orgid,depart.depNm AS depNm,post.postCd AS postCd,post.postNm AS postNm,post.description AS description ,post.dep_id AS depId FROM ess_department depart,ess_organization org,ess_post post WHERE depart.org_id=org.id AND post.dep_id=depart.id AND post.isDeleted!=1
SELECT post.id AS id,etp.etpNm AS orgNm,etp.id AS orgid,depart.depNm AS depNm,post.postCd AS postCd,post.postNm AS postNm,post.description AS description ,post.dep_id AS depId FROM ess_department depart,ess_enterprise etp,ess_post post WHERE depart.ept_id=etp.id AND post.dep_id=depart.id AND post.isDeleted!=1
and post.postNm like '%${postNm}%'
order by post.createTime DESC limit ${startRecord},${pageSize}

select * from ess_department where 1=1

    and org_id =#{orgid}

AND isDeleted !=1

SELECT emp.empCd AS empcd,emp.empNm AS empnm,org.orgNm AS orgNm,depart.depNm AS depNm FROM ess_employee_dept
dept,ess_employee emp,ess_organization org ,ess_department depart WHERE 1=1
AND dept.emp_id=emp.empCd AND dept.dep_id =depart.id AND org.orgCd=depart.org_id AND org.orgCd = #{orgid}

    AND dept.dep_id==#{depid}

SELECT post.id AS id,post.postNm AS postnm FROM ess_post post LEFT JOIN ess_department department ON
department.id = post.dep_id LEFT JOIN ess_organization organization ON organization.id = department.org_id WHERE
organization.id = #{orgId}

    AND dep_id=#{depId}


    AND postNm like '%${postNm}%'
