mapper.select:
select * from ess_role where 1=1

	and roleCd like '%${roleCd}%'


	and roleNm like '%${roleNm}%'


	and isDefault = ${isDefault}

order by roleCd asc limit ${startRecord},${pageSize}

mapper.delete:
delete from ess_role where
id in

	#{id}

mapper.select:
SELECT r.id AS roleId, r.roleCd, r.roleNm, r.description, GROUP_CONCAT( CONCAT(e.empNm, '/', e.account) ) AS empNms, GROUP_CONCAT(e.id) AS emp_id FROM ess_role r LEFT JOIN ess_employee_role re ON r.id = re.role_id LEFT JOIN ess_employee e ON re.emp_id = e.id WHERE 1 = 1
and r.roleNm like '%${roleNm}%'
and r.id = #{roleId}
GROUP BY r.id limit ${startRecord},${pageSize}

SELECT e.id, o.orgNm, GROUP_CONCAT(d.depNm) AS depNm, GROUP_CONCAT(p.postNm) AS postNm, e.empNm, e.account FROM ess_employee e LEFT JOIN ess_employee_dept ed ON e.id = ed.emp_id LEFT JOIN ess_department d ON ed.dep_id = d.id LEFT JOIN ess_employee_post ep ON e.id = ep.emp_id LEFT JOIN ess_post p ON ep.post_id = p.id LEFT JOIN ess_organization o ON e.id = o.admin_empid where 1 = 1
and o.id = #{orgId}
and d.id = #{depId}
and e.empNm = #{empNm}
GROUP BY e.id limit ${startRecord},${pageSize}

SELECT count(e.id) FROM ess_employee e LEFT JOIN ess_employee_dept ed ON e.id = ed.emp_id LEFT JOIN ess_department d ON ed.dep_id = d.id LEFT JOIN ess_employee_post ep ON e.id = ep.emp_id LEFT JOIN ess_post p ON ep.post_id = p.id LEFT JOIN ess_organization o ON e.id = o.admin_empid where 1 = 1
and o.id = #{orgId}
and d.id = #{depId}
and e.empNm = #{empNm}
