mapper.select:
SELECT  FROM user_oper_log_${curDate}

    content like CONCAT(CONCAT('%',#{params.content},'%')) 
    AND operBy  like CONCAT(CONCAT('%',#{params.operBy},'%'))
    AND operType = #{params.operType} 
    AND ipAddr = #{params.ipAddr} 
    AND operTime > #{params.operTime} 
    AND operTime < #{params.operTime} 

ORDER BY operTime ASC

    limit #{params.startRecord}, #{params.pageSize}

SELECT COUNT(id) FROM user_oper_log_${curDate}

    content like CONCAT(CONCAT('%',#{params.content},'%')) 
    AND operBy  like CONCAT(CONCAT('%',#{params.operBy},'%'))
    AND operType = #{params.operType} 
    AND ipAddr = #{params.ipAddr} 
    AND operTime > #{params.operTime} 
    AND operTime < #{params.operTime}
