mapper.select:
SELECT danger.*,area.areaNm,org.orgNm AS orgNm ,etp.etpNm AS etpNm FROM ess_area AREA ,ess_organization org
,dr_danger danger ,driver dr,ess_enterprise etp WHERE area.id =org.area_id AND danger.ownerGener=org.id AND
danger.driverIdcard = dr.idcard AND dr.mgrEnterpriseId =etp.id

    and org.id in
    
        #{item.id}
    


    and area.id = #{areaId}


    and dr.mgrEnterpriseId = #{etpId}


    and danger.dangerType != 0


    and danger.dangerType = #{dangertype}


    and danger.fullStudyState = 1


    and danger.overdueExamineState = 1


    and danger.overdueProofState = 1

order by danger.dangerType DESC
limit ${startRecord},${pageSize}

SELECT COUNT(1) FROM ess_area AREA ,ess_organization org ,dr_danger danger ,driver dr,ess_enterprise etp WHERE area.id =org.area_id AND danger.ownerGener=org.id AND danger.driverIdcard = dr.idcard AND dr.mgrEnterpriseId =etp.id
and org.id in
#{item.id}
and area.id = #{areaId}
and danger.dangerType = #{dangertype}
and danger.dangerType != 0
and danger.fullStudyState = 1
and danger.overdueExamineState = 1
and danger.overdueProofState = 1
