SELECT d.id, d.adress, a.areaNm,
d.idcard, d.mobilephone, en.etpNm, d.permiCar, d.firstReciveTime, d.nextExamineTime, d.state, d.mgrDepart, d.telphone, d.scoreTotal, d.examineEffectEndTime, d.effectStartTime, d.effectEndTime, d.state1 FROM ess_area a LEFT JOIN driver d ON a.id = d.mgrDepartAreaId LEFT JOIN ess_enterprise en ON d.mgrEnterpriseId = en.id
WHERE 1=1
WHERE en.id in
#{item}
AND en.etpNm like '%${etpNm}%'
AND a.areaNm = #{areaNm}
AND
AND d.idcard = #{idcard}
AND d.permiCar like '%${permicar}%'
AND d.id = #{id}
AND d.mobilephone = #{mobilephone}
AND d.firstReciveTime > #{fiString}
AND d.firstReciveTime < #{laString}
AND d.nextexaminetime > #{fistShString}
AND d.nextexaminetime < #{laShString}
AND (d.isDelete is null OR d.isDelete !=1) LIMIT ${startRecord},${pageSize}

SELECT
COUNT(d.id)
FROM
ess_area a
LEFT JOIN driver d ON a.id =
d.mgrDepartAreaId
LEFT JOIN ess_enterprise en ON d.mgrEnterpriseId =
en.id

	
		WHERE 1=1
	
	
		WHERE en.id in
		
			#{item}
		
	


	AND en.etpNm like '%${etpNm}%'


	AND a.areaNm = #{areaNm}


	AND trim(REPLACE(`name`, ' ', '')) LIKE
	trim(REPLACE('%${name}%', ' ', ''))


	AND d.idcard = #{idcard}


	AND d.permiCar like '%${permicar}%'


	AND d.id = #{id}


	AND d.mobilephone = #{mobilephone}


	AND d.firstReciveTime > #{fiString}


	AND d.firstReciveTime < #{laString}


	AND d.nextexaminetime > #{fistShString}


	AND d.nextexaminetime < #{laShString}

AND (d.isDelete is null OR d.isDelete !=1)

SELECT e.etpNm, replace(vd.name,' ','') name, vd.illicitTime, vd.illicitAdress, vd.illicitScore, vd.illicitAmount, vd.mgrDepart, vd.drIllicitState
FROM vehi_dr_illicit2018 vd LEFT JOIN vehicle v ON v.id = vd.vehicle_id LEFT JOIN ess_enterprise e ON e.id = v.mgrEnterpriseId
where 1=1
where e.id in
#{item}
and vd.idcard = #{idcard} and (vd.isDeleted is null OR vd.isDeleted !=1) limit ${startRecord} , ${pageSize}

        SELECT net.id,d.idcard,d.permicar AS permitcar,d.mgrdepart,d.mgrdepartareaid, net.WFSJ AS illicittime ,net.WFJFS AS illicitscore ,net.DSR AS name ,ict.illegal_name AS illicit,net.CLJGMC AS pickdepartmentdesc,net.id,d.permiCar,d.mgrDepart,d.mgrDepartareaid ,ent.etpNm
