SELECT en.id, en.ownerTransport, a.areaNm, en.etpNm, en.etpCd, en.etpshortNm, en.etpLawer, en.lawerTel, en.etpSafer, en.saferTel, en.ownerTraffic, en.ownerTransport, CASE en.state WHEN 1 THEN '审核通过' WHEN 0 THEN '待审核' WHEN -1 THEN '审核未通过' end as state, en.etpTel, en.etpAdress, en.remark, en.etpCd, en.etpType, en.issueCardDate, en.roadTransport FROM ess_organization eo LEFT JOIN ess_enterprise en ON en.org_id = eo.id LEFT JOIN ess_area a ON eo.area_id = a.id
WHERE 1=1
WHERE en.id in
#{item}
AND en.id != ''
AND a.areaNm = #{areaNm}
AND en.id = #{id}
AND en.etpNm like '%${etpNm}%'
AND en.ownerTraffic = #{ownerTraffic}
AND en.ownerTransport = #{ownerTransport}
AND en.etpCd = #{etpCd}
AND (en.isDeleted is null OR en.isDeleted !=1) LIMIT ${startRecord} , ${pageSize}

SELECT
COUNT(en.id)
FROM
ess_organization eo
LEFT JOIN ess_enterprise en
ON en.org_id = eo.id
LEFT JOIN ess_area a ON eo.area_id = a.id

	
		WHERE 1=1
	
	
		WHERE e.id in
		
			#{item}
		
	

	AND en.id != ''

	AND a.areaNm = #{areaNm}


	AND en.id = #{id}


	AND en.etpNm like '%${etpNm}%'


	AND en.ownerTraffic = #{ownerTraffic}


	AND en.ownerTransport = #{ownerTransport}


	AND en.etpCd = #{etpCd}

AND (en.isDeleted is null OR en.isDeleted !=1)

