WHERE 1=1
WHERE e.id in
#{item}
AND e.id != ''
AND o.orgNm =#{orgNm}
AND e.etpNm like '%${etpNm}%'
AND v.vehiType =#{vehiType}
AND e.id =#{id}
AND (e.isDeleted is null OR e.isDeleted !=1) LIMIT ${startRecord} , ${pageSize}

