mapper.select:
SELECT e.id, e.etpNm, COUNT(v.id) AS vCount FROM ess_organization o LEFT JOIN ess_enterprise e ON o.id = e.org_id LEFT JOIN vehicle v ON e.id = v.mgrEnterpriseId LEFT JOIN vehi_danger vd ON v.id = vd.vehicle_id
where 1=1
where e.id in
#{item}
and o.orgNm = #{orgNm}
and e.etpNm like '%${etpNm}%'
and v.vehiType = #{vehiType}
AND e.etpNm != '' AND o.isDeleted IS NULL OR o.isDeleted != 1 GROUP BY e.etpNm

SELECT e.etpNm, COUNT(vd.scrappedState) AS vCount FROM ess_organization o LEFT JOIN ess_enterprise e ON o.id = e.org_id LEFT JOIN vehicle v ON e.id = v.mgrEnterpriseId LEFT JOIN vehi_danger vd ON v.id = vd.vehicle_id
where 1=1
where e.id in
#{item}
and o.orgNm = #{orgNm}
and e.etpNm like '%${etpNm}%'
and v.vehiType = #{vehiType}
and vd.scrappedState = 1 AND e.etpNm != '' AND o.isDeleted IS NULL OR o.isDeleted != 1 GROUP BY e.etpNm

SELECT e.etpNm, COUNT(vd.illicitState) AS vCount FROM ess_organization o LEFT JOIN ess_enterprise e ON o.id = e.org_id LEFT JOIN vehicle v ON e.id = v.mgrEnterpriseId LEFT JOIN vehi_danger vd ON v.id = vd.vehicle_id
where 1=1
where e.id in
#{item}
and o.orgNm = #{orgNm}
and e.etpNm like '%${etpNm}%'
and v.vehiType = #{vehiType}
and vd.illicitState = 1 AND e.etpNm != '' AND o.isDeleted IS NULL OR o.isDeleted != 1 GROUP BY e.etpNm

SELECT e.etpNm, COUNT(vd.overdueExamineState) AS vCount FROM ess_organization o LEFT JOIN ess_enterprise e ON o.id = e.org_id LEFT JOIN vehicle v ON e.id = v.mgrEnterpriseId LEFT JOIN vehi_danger vd ON v.id = vd.vehicle_id
where 1=1
where e.id in
#{item}
and o.orgNm = #{orgNm}
and e.etpNm like '%${etpNm}%'
and vd.vehiType = #{vehiType}
and vd.overdueExamineState = 1 AND e.etpNm != '' AND o.isDeleted IS NULL OR o.isDeleted != 1 GROUP BY e.etpNm
