SELECT d.id, d.adress, a.areaNm,
d.idcard, d.mobilephone, en.etpNm, d.permiCar, d.firstReciveTime, d.nextExamineTime, d.state, d.mgrDepart, d.telphone, d.scoreTotal, d.examineEffectEndTime, d.effectStartTime, d.effectEndTime, d.state1 FROM ess_area a LEFT JOIN driver d ON a.id = d.mgrDepartAreaId LEFT JOIN ess_enterprise en ON d.mgrEnterpriseId = en.id LEFT JOIN ess_organization o ON o.id = en.org_id
WHERE 1=1
WHERE a.id in
#{item}
WHERE en.id = #{etpId}
AND en.etpNm like '%${etpNm}%'
AND o.id in
#{item}
AND a.areaNm = #{areaNm}
AND
AND d.idcard = #{idcard}
AND d.permiCar like '%${permicar}%'
AND d.id = #{id}
AND d.mobilephone = #{mobilephone}
AND d.firstReciveTime > #{fiString}
AND d.firstReciveTime < #{laString}
AND d.nextexaminetime > #{fistShString}
AND d.nextexaminetime < #{laShString}
AND (d.isDelete is null OR d.isDelete !=1) LIMIT ${startRecord},${pageSize}

SELECT
COUNT(d.id)
FROM
ess_area a
LEFT JOIN driver d ON a.id =
d.mgrDepartAreaId
LEFT JOIN ess_enterprise en ON d.mgrEnterpriseId =
en.id
LEFT JOIN ess_organization
o ON o.id = en.org_id

	
		WHERE 1=1
	
	
		WHERE a.id in
		
			#{item}
		
	
	
		WHERE en.id = #{etpId}
	


	AND en.etpNm like '%${etpNm}%'


	AND o.id in
	
		#{item}
	


	AND a.areaNm = #{areaNm}


	AND trim(REPLACE(`name`, ' ', '')) LIKE
	trim(REPLACE('%${name}%', ' ', ''))


	AND d.idcard = #{idcard}


	AND d.permiCar like '%${permicar}%'


	AND d.id = #{id}


	AND d.mobilephone = #{mobilephone}


	AND d.firstReciveTime > #{fiString}


	AND d.firstReciveTime < #{laString}


	AND d.nextexaminetime > #{fistShString}


	AND d.nextexaminetime < #{laShString}

AND (d.isDelete is null OR d.isDelete !=1)

		SELECT illicit.id,illicit.JSZH as
		idcard,illicit.mgrdepartareaid,illicit.illicitdesc AS
		illicit,illicittime ,illicit.illicitScore ,illicit.DSR AS name
		,illicit.illicit,illicit.pickdepartmentdesc,
		illicit.areaNm,illicit.mgrDepartareaid,illicit.etpNm
		,illicit.illicitamount,illicit.illicitadress
		FROM illicit_2018 illicit
		JOIN (select id FROM illicit_2018 where
		(disposeSign ="" OR disposeSign
		IS NULL )
		
			AND mgrDepartAreaId IN
			
				#{item}
			
		
		
			AND mgrEnterpriseId = #{etpId}
		
		
			AND id = #{id}
		
		
			AND JSZH = #{idCard}
		
		
			AND dsr LIKE '%${name}%'
		
		
			AND etpNm LIKE '%${name}%'
		
		
			AND illicittime >= #{startTime}
		
		
			AND etpNm <= #{endTime}
		

		LIMIT ${startRecord} ,${pageSize}) tmp ON tmp.id = illicit.id

