COUNT(en.id)
FROM
ess_area a
LEFT JOIN ess_organization eo ON
eo.area_id = a.id
LEFT JOIN ess_enterprise en ON en.org_id = eo.id

	
		WHERE 1=1
	
	
		WHERE a.id in
		
			#{item}
		
	
	
		WHERE en.id = #{etpId}
	

AND en.id != ''

	AND a.areaNm = #{areaNm}


	AND en.id = #{id}


	AND en.etpNm like '%${etpNm}%'


	AND en.ownerTraffic = #{ownerTraffic}


	AND en.ownerTransport = #{ownerTransport}


	AND en.etpCd = #{etpCd}

AND (en.isDeleted is null OR en.isDeleted !=1)

SELECT e.etpNm, v.vehiNo, v.nature, v.vehiType, v.effectStartTime, v.effectEndTime, v.updateTime FROM ess_organization o LEFT JOIN ess_enterprise e ON o.id = e.org_id LEFT JOIN vehicle v ON e.id = v.mgrEnterpriseId LEFT JOIN vehi_danger vd ON v.id = vd.vehicle_id
WHERE 1=1
WHERE e.id in
#{item}
AND e.id != ''
AND o.orgNm =#{orgNm}
AND e.etpNm like '%${etpNm}%'
AND v.vehiType =#{vehiType}
AND e.id =#{id}
AND (e.isDeleted is null OR e.isDeleted !=1) LIMIT ${startRecord} , ${pageSize}

