mapper.select:
		SELECT COUNT(1) FROM ess_enterprise WHERE 1=1
		
			and etpNm = #{etpnm}
		
		
			and state = #{state}
		
		
			and left(createTime,10) >= #{referStartTime}
		
		
			and left(createTime,10) <= #{referEndTime}
		

		
			and left(updateTime,10) >= #{checkStartTime}
		
		
			and left(updateTime,10) <= #{checkEndTime}

		SELECT
		etpNm,etpAdress,etpTel,etpSafer,saferTel,etpLawer,lawerTel,createTime,updateTime,state
		FROM ess_enterprise
		WHERE 1=1 and (isDeleted is null or isDeleted = 0)
		
			and etpNm = #{etpnm}
		
		
			and state = #{state}
		
		
			and left(createTime,10) >= #{referStartTime}
		
		
			and left(createTime,10) <= #{referEndTime}
		

		
			and left(updateTime,10) >= #{checkStartTime}
		
		
			and left(updateTime,10) <= #{checkEndTime}
		
		order by createTime desc
		limit ${startRecord},${pageSize}

SELECT v.id, v.vehiNo, v.vehiType, CASE v.vehiNoType WHEN '01' THEN '大型车' ELSE '小型车' END AS vehiNoType, v.nature, v.firstReciveTime, v.examineEffectEndTime as effectEndTime , v.owner, v.mobilephone, v.telphone, v.mgrDepart, CASE v.state WHEN 'A' THEN '正常' WHEN 'G' THEN '违法未处理' WHEN 'Q' THEN '逾期未检验' WHEN 'P' THEN '达到报废标准公告牌证作废' WHEN 'K' THEN '查封' WHEN 'O' THEN '锁定' WHEN 'L' THEN '扣留' WHEN 'J' THEN '嫌疑车辆' WHEN 'N' THEN '事故逃逸' WHEN 'D' THEN '停驶' WHEN 'E' THEN '注销' WHEN 'H' THEN '海管监管' WHEN 'C' THEN '被强盗' WHEN 'B' THEN '转出' WHEN 'M' THEN '达到报废标准' ELSE '不正常' END AS state, a.areaNm , v.ownership, v.adress, v.examineEffectEndTime
FROM ess_area a LEFT JOIN vehicle v ON a.id = v.mgrDepartAreaId LEFT JOIN ess_enterprise e ON v.mgrEnterpriseId = e.id
where v.vehiNo= #{vehiNo}
where 1=1
where e.id in
#{item}
and (v.isDeleted is null OR v.isDeleted !=1)
and e.etpNm like '%${etpNm}%'
and a.areaNm= #{areaNm}
and v.id= #{id}
and v.id != '' limit ${startRecord},${pageSize}

SELECT count(v.id ) FROM ess_area a LEFT JOIN vehicle v ON a.id = v.mgrDepartAreaId LEFT JOIN ess_enterprise e ON v.mgrEnterpriseId = e.id
where v.vehiNo= #{vehiNo}
where 1=1
where e.id in
#{item}
and (v.isDeleted is null OR v.isDeleted !=1)
and e.etpNm like '%${etpNm}%'
and a.areaNm= #{areaNm}
and v.id= #{id}
and v.id != ''

SELECT d.id, d. NAME, d.idcard, d.mobilephone, d.permiCar, d.firstReciveTime, d.nextExamineTime, CASE d.state WHEN 'A' THEN '正常' WHEN 'B' THEN '超分' WHEN 'C' THEN '转出' WHEN 'D' THEN '暂扣' WHEN 'E' THEN '撤销' WHEN 'F' THEN '吊销' WHEN 'G' THEN '注销' WHEN 'H' THEN '违法未处理' WHEN 'I' THEN '事故未处理' WHEN 'J' THEN '停止使用' WHEN 'K' THEN '扣押' WHEN 'L' THEN '锁定' WHEN 'M' THEN '逾期未换证' WHEN 'N' THEN '延期换证' WHEN 'P' THEN '延期体检' WHEN 'R' THEN '注销可恢复' WHEN '延期审验' THEN '正常' WHEN 'U' THEN '扣留' WHEN 'S' THEN '逾期未审验' else '不明确' end as state, d.mgrDepart, d.telphone, d.scoreTotal, d.examineEffectEndTime, d.effectStartTime, d.effectEndTime, e.etpNm, a.areaNm, CASE d.state1 WHEN 'A' THEN '正常' WHEN 'B' THEN '超分' WHEN 'C' THEN '转出' WHEN 'D' THEN '暂扣' WHEN 'E' THEN '撤销' WHEN 'F' THEN '吊销' WHEN 'G' THEN '注销' WHEN 'H' THEN '违法未处理' WHEN 'I' THEN '事故未处理' WHEN 'J' THEN '停止使用' WHEN 'K' THEN '扣押' WHEN 'L' THEN '锁定' WHEN 'M' THEN '预期未换证' WHEN 'N' THEN '延期换证' WHEN 'P' THEN '延期体检' WHEN 'R' THEN '注销可恢复' WHEN 'T' THEN '延期审验' WHEN 'U' THEN '扣留' WHEN 'S' THEN '逾期未审验' else '不明确' END as state1 FROM driver d LEFT JOIN ess_enterprise e ON d.mgrEnterpriseId = e.id LEFT JOIN ess_area a ON d.mgrDepartAreaId = a.id
where d.idcard= #{idcard}
where 1=1
where e.id in
#{item}
and e.etpNm like '%${etpNm}%'
and a.areaNm= #{areaNm}
and d.id= #{id}
and d.id !='' and (d.isDelete is null OR d.isDelete !=1) limit ${startRecord},${pageSize}

SELECT count(d.id) FROM driver d LEFT JOIN ess_enterprise e ON d.mgrEnterpriseId = e.id LEFT JOIN ess_area a ON d.mgrDepartAreaId = a.id
where d.idcard= #{idcard}
where 1=1
where e.id in
#{item}
and e.etpNm like '%${etpNm}%'
and a.areaNm= #{areaNm}
and d.id= #{id}
and d.id !='' and (d.isDelete is null OR d.isDelete !=1)
