mapper.select:
  	SELECT COUNT(1) FROM ess_enterprise WHERE 1=1
  		
			and etpNm = #{etpnm}
		
  		
			and state = #{state}
		
  		
			and left(createTime,10) >= #{referStartTime}
		
  		
			and left(createTime,10) <= #{referEndTime}
		
		
		
			and left(updateTime,10) >= #{checkStartTime}
		
  		
			and left(updateTime,10) <= #{checkEndTime}

  	SELECT etpNm,etpAdress,etpTel,etpSafer,saferTel,etpLawer,lawerTel,createTime,updateTime,state 
  	FROM ess_enterprise 
  	WHERE 1=1 and (isDeleted is null or isDeleted = 0)
  		
			and etpNm = #{etpnm}
		
  		
			and state = #{state}
		
  		
			and left(createTime,10) >= #{referStartTime}
		
  		
			and left(createTime,10) <= #{referEndTime}
		
		
		
			and left(updateTime,10) >= #{checkStartTime}
		
  		
			and left(updateTime,10) <= #{checkEndTime}
		
		order by createTime desc
		limit ${startRecord},${pageSize}
