mapper.select:
        SELECT COUNT(1)
        from (
		   select * from (
		   		
		   			
		   				SELECT ve.vehiNo, ve.vehiNoType, ve.mgrDepartAreaId, ve.mgrEnterpriseId FROM vehicle ve
		    			JOIN ( SELECT id FROM vehicle LIMIT ${startRecord},${pageSize} ) temp on ve.id = temp.id 
		   			
		   			
		   				SELECT v.id FROM vehicle v LEFT JOIN ess_enterprise e ON v.mgrEnterpriseId=e.id LEFT JOIN ess_organization org on e.org_id=org.id WHERE org.id =#{id} LIMIT ${startRecord},${pageSize} ) 
						temp on ve.id = temp.id  LEFT JOIN ess_enterprise e ON ve.mgrEnterpriseId=e.id LEFT JOIN ess_organization org on e.org_id=org.id WHERE org.id =#{id}
		   			
		   			
		   				SELECT ve.vehiNo, ve.vehiNoType, ve.mgrDepartAreaId, ve.mgrEnterpriseId FROM vehicle ve 
			    		JOIN ( SELECT id FROM vehicle v WHERE v.mgrEnterpriseId=#{id} LIMIT ${startRecord},${pageSize} ) 
						temp on ve.id = temp.id WHERE ve.mgrEnterpriseId=#{id}
		   			
		   		
		    ) ve 
		   LEFT JOIN (
		     SELECT vio.id ,vio.HPZL,vio.HPHM,vio.WFDZ,vio.WFXW,NULL AS CLBJ,vio.JKBJ,vio.CLJG AS CJJG,vio.CLJGMC AS CJJGMC,vio.WFSJ,vio.FKJE,vio.CLSJ,vio.JDSBH,vio.WFBH,vio.JSZH,vio.WFJFS,vio.DSR,vio.CFZL 
				 FROM ${netTrffViolation} vio ) net1 on net1.hpzl = ve.vehiNoType and net1.hphm = CONCAT('渝',ve.vehiNo)
		   UNION all
		   select * from (
		    SELECT ve.vehiNo, ve.vehiNoType, ve.mgrDepartAreaId, ve.mgrEnterpriseId FROM vehicle ve
		    JOIN ( SELECT id FROM vehicle LIMIT ${startRecord},${pageSize} ) temp on ve.id = temp.id ) ve 
		   LEFT JOIN(	
		      SELECT sur.id ,sur.HPZL,sur.HPHM,sur.WFDZ,sur.WFXW,sur.CLBJ,sur.JKBJ,sur.CJJG,sur.CJJGMC,sur.WFSJ,sur.FKJE,sur.CLSJ,sur.JDSBH,NULL AS WFBH,NULL AS JSZH,NULL AS WFJFS,NULL AS DSR,NULL AS CFZL 
					FROM ${netTrffSurveil} sur WHERE sur.clbj='0' ) net2 on net2.hpzl = ve.vehiNoType and net2.hphm = CONCAT('渝', ve.vehiNo)
		  ) net
		LEFT JOIN ess_area area ON net.mgrDepartAreaId=area.id 
		LEFT JOIN ess_enterprise ess ON net.mgrEnterpriseId=ess.id 
		LEFT JOIN illegal_comparison_table tablei ON net.WFXW=tablei.id 
        WHERE 1=1
        
            and cit.mgrDepartAreaId = #{mgrDepartAreaId}
        
        
            and ve.vehiNo = #{vehiNo}
        
        
            and cit.ownership LIKE '%${etpNm}%'
        
        
            and cit.illicit = #{illicit}
        
        
            and cit.vehiState = #{state}
        
        
            and cit.pickDepartmentDesc LIKE '%${pickDepartmentDesc}%'
        
        
            and ve.vehiNoType = #{vehiNoType}
        
        
            and cit.illicitTime >= #{startDate}
        
        
            and cit.illicitTime <= #{endDate}

        select net.id,ess.etpNm,net.hphm as vehiNo,CASE net.hpzl WHEN '01' THEN '大型车' WHEN '02' THEN '小型车' ELSE '' END vehiNoTypeText,net.hpzl as vehiNoType,
		net.wfsj as illicitTime,net.wfxw as illicit,net.wfdz as illicitAdress,tablei.illegal_name AS illicitDesc,CASE net.CLBJ WHEN '0' THEN '未处理'
		WHEN '1' THEN '已处理' ELSE '' END disposeSign,net.fkje AS illicitAmount,net.WFJFS as illicitScore,CASE net.CFZL WHEN '1' THEN '警告' WHEN '2' THEN '罚款' WHEN '3' THEN '暂扣驾驶证' WHEN '4' THEN '吊销驾驶证'
		WHEN '5' THEN '行政拘留' WHEN '6' THEN '撤销机动车驾驶许可' WHEN '7' THEN '撤销机动车登记' WHEN '9' THEN '其他' ELSE '' END disposeType,
		CASE net.CLBJ WHEN '0' THEN '未处理' WHEN '1' THEN '已处理' ELSE '' END vehiState from (
		   select * from (
		   		
		   			
		   				SELECT ve.vehiNo, ve.vehiNoType, ve.mgrDepartAreaId, ve.mgrEnterpriseId FROM vehicle ve
		    			JOIN ( SELECT id FROM vehicle LIMIT ${startRecord},${pageSize} ) temp on ve.id = temp.id 
		   			
		   			
		   				SELECT v.id FROM vehicle v LEFT JOIN ess_enterprise e ON v.mgrEnterpriseId=e.id LEFT JOIN ess_organization org on e.org_id=org.id WHERE org.id =#{id} LIMIT ${startRecord},${pageSize} ) 
						temp on ve.id = temp.id  LEFT JOIN ess_enterprise e ON ve.mgrEnterpriseId=e.id LEFT JOIN ess_organization org on e.org_id=org.id WHERE org.id =#{id}
		   			
		   			
		   				SELECT ve.vehiNo, ve.vehiNoType, ve.mgrDepartAreaId, ve.mgrEnterpriseId FROM vehicle ve 
			    		JOIN ( SELECT id FROM vehicle v WHERE v.mgrEnterpriseId=#{id} LIMIT ${startRecord},${pageSize} ) 
						temp on ve.id = temp.id WHERE ve.mgrEnterpriseId=#{id}
		   			
		   		
		    ) ve 
		   LEFT JOIN (
		     SELECT vio.id ,vio.HPZL,vio.HPHM,vio.WFDZ,vio.WFXW,NULL AS CLBJ,vio.JKBJ,vio.CLJG AS CJJG,vio.CLJGMC AS CJJGMC,vio.WFSJ,vio.FKJE,vio.CLSJ,vio.JDSBH,vio.WFBH,vio.JSZH,vio.WFJFS,vio.DSR,vio.CFZL 
				 FROM ${netTrffViolation} vio ) net1 on net1.hpzl = ve.vehiNoType and net1.hphm = CONCAT('渝',ve.vehiNo)
		   UNION all
		   select * from (
		    SELECT ve.vehiNo, ve.vehiNoType, ve.mgrDepartAreaId, ve.mgrEnterpriseId FROM vehicle ve
		    JOIN ( SELECT id FROM vehicle LIMIT ${startRecord},${pageSize} ) temp on ve.id = temp.id ) ve 
		   LEFT JOIN(	
		      SELECT sur.id ,sur.HPZL,sur.HPHM,sur.WFDZ,sur.WFXW,sur.CLBJ,sur.JKBJ,sur.CJJG,sur.CJJGMC,sur.WFSJ,sur.FKJE,sur.CLSJ,sur.JDSBH,NULL AS WFBH,NULL AS JSZH,NULL AS WFJFS,NULL AS DSR,NULL AS CFZL 
					FROM ${netTrffSurveil} sur WHERE sur.clbj='0' ) net2 on net2.hpzl = ve.vehiNoType and net2.hphm = CONCAT('渝', ve.vehiNo)
		  ) net
		LEFT JOIN ess_area area ON net.mgrDepartAreaId=area.id 
		LEFT JOIN ess_enterprise ess ON net.mgrEnterpriseId=ess.id 
		LEFT JOIN illegal_comparison_table tablei ON net.WFXW=tablei.id 
		ORDER BY net.WFSJ  DESC limit ${startRecord},${pageSize}
        WHERE 1=1
        
            and cit.mgrDepartAreaId = #{mgrDepartAreaId}
        
        
            and ve.vehiNo = #{vehiNo}
        
        
            and cit.ownership LIKE '%${etpNm}%'
        
        
            and cit.illicit = #{illicit}
        
        
            and cit.vehiState = #{state}
        
        
            and cit.pickDepartmentDesc LIKE '%${pickDepartmentDesc}%'
        
        
            and ve.vehiNoType = #{vehiNoType}
        
        
            and cit.illicitTime >= #{startDate}
        
        
            and cit.illicitTime <= #{endDate}

SELECT net.id,ess.etpNm,net.hphm as vehiNo,CASE net.hpzl WHEN '01' THEN '大型车' WHEN '02' THEN '小型车' ELSE '' END vehiNoTypeText,net.hpzl as vehiNoType, net.wfsj as illicitTime,net.wfxw as illicit,net.wfdz as illicitAdress,tablei.illegal_name AS illicitDesc,CASE net.CLBJ WHEN '0' THEN '未处理' WHEN '1' THEN '已处理' ELSE '' END disposeSign,net.fkje AS illicitAmount,net.WFJFS as illicitScore,CASE net.CFZL WHEN '1' THEN '警告' WHEN '2' THEN '罚款' WHEN '3' THEN '暂扣驾驶证' WHEN '4' THEN '吊销驾驶证' WHEN '5' THEN '行政拘留' WHEN '6' THEN '撤销机动车驾驶许可' WHEN '7' THEN '撤销机动车登记' WHEN '9' THEN '其他' ELSE '' END disposeType, CASE net.CLBJ WHEN '0' THEN '未处理' WHEN '1' THEN '已处理' ELSE '' END vehiState FROM (
SELECT sur.id,sur.HPZL,sur.HPHM,sur.WFDZ,sur.WFXW,sur.CLBJ,sur.JKBJ,sur.CJJG,sur.CJJGMC,sur.WFSJ,sur.FKJE,sur.CLSJ,sur.JDSBH,NULL AS WFBH,NULL AS JSZH,NULL AS WFJFS,NULL AS DSR,NULL AS CFZL FROM ${tableNmae} sur WHERE sur.ID=#{id}
SELECT vio.id,vio.HPZL,vio.HPHM,vio.WFDZ,vio.WFXW,NULL AS CLBJ,vio.JKBJ,vio.CLJG AS CJJG,vio.CLJGMC AS CJJGMC,vio.WFSJ,vio.FKJE,vio.CLSJ,vio.JDSBH,vio.WFBH,vio.JSZH,vio.WFJFS,vio.DSR,vio.CFZL FROM ${tableNmae} vio WHERE vio.ID=#{id}
) net LEFT JOIN (SELECT * FROM vehicle v WHERE v.vehiNo ='')ve ON ve.vehiNo=net.hphm LEFT JOIN ess_area area ON ve.mgrDepartAreaId=area.id LEFT JOIN ess_enterprise ess ON ve.mgrEnterpriseId=ess.id LEFT JOIN illegal_comparison_table tablei ON net.WFXW=tablei.id

mapper.update:
