mapper.select:
select count(1) from vehi_illicit cit LEFT JOIN vehicle ve ON cit.vehicle_id=ve.id	
				WHERE (cit.isDeleted is null or cit.isDeleted !=1) and ve.mgrEnterpriseId in
		  
	        #{item}  
	      
		
			and cit.mgrDepartAreaId = #{mgrDepartAreaId}
		
		
			and ve.vehiNo = #{vehiNo}
		
		
			and ve.etpNm = #{etpNm}
		
		
			and cit.illicit = #{illicit}
		
		
			and ve.state = #{state}
		
		
			and cit.pickDepartmentDesc = #{pickDepartmentDesc}
		
		
			and ve.vehiNoType = #{vehiNoType}
		
		
			and left(cit.illicitTime,10) >= #{startTime}
		
		
			and left(cit.illicitTime,10) <= #{endTime}

SELECT cit.id,cit.vehiNo,
		CASE cit.vehiNoType WHEN '01' THEN '大型车' WHEN '02' THEN '小型车' ELSE '' END vehiNoType,ve.vehiNoType as vehiNoTypeText,
		cit.nature,cit.ownership,cit.owner,cit.adress,cit.mgrDepart,cit.mgrDepartAreaId,
	   cit.vehicle_id as vehicleId,cit.illicitTime,cit.illicitScore,cit.illicitAmount,cit.illicitAdress,cit.illicit,
	   cit.illicitDesc,cit.pickDepartment,cit.pickDepartmentDesc,
     CASE cit.state WHEN 'G' THEN '违法未处理' WHEN 'A' THEN '正常' WHEN 'O' THEN '锁定' WHEN 'N' THEN '事故逃逸'
	 WHEN 'M' THEN '达到报废标准' WHEN 'L' THEN '扣留' WHEN 'K' THEN '查封' WHEN 'J' THEN '嫌疑车' 
	 WHEN 'D' THEN '停驶' WHEN 'E' THEN '注销' WHEN 'H' THEN '海关监管' WHEN 'C' THEN '被盗抢' 
	 WHEN 'B' THEN '转出' WHEN 'P' THEN '达到报废标准公告牌证作废' WHEN 'Q' THEN '逾期未检验' 
	 ELSE '其他' END  state
	   ,cit.remark,ess.etpNm
		FROM vehi_illicit cit LEFT JOIN vehicle ve ON cit.vehicle_id=ve.id
		LEFT JOIN ess_enterprise ess ON ve.mgrEnterpriseId=ess.id
		WHERE (cit.isDeleted is null or cit.isDeleted !=1) and ess.id in
		  
	        #{item}  
	      
		
			and cit.mgrDepartAreaId = #{mgrDepartAreaId}
		
		
			and ve.vehiNo = #{vehiNo}
		
		
			and ve.etpNm = #{etpNm}
		
		
			and cit.illicit = #{illicit}
		
		
			and ve.state = #{state}
		
		
			and cit.pickDepartmentDesc = #{pickDepartmentDesc}
		
		
			and ve.vehiNoType = #{vehiNoType}
		
		
			and left(cit.illicitTime,10) >= #{startTime}
		
		
			and left(cit.illicitTime,10) <= #{endTime}
		
		order by illicitTime desc
	
		limit ${startRecord},${pageSize}

